import org.omg.Messaging.SyncScopeHelper;

public class EjemplosStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String miFrase="Hola mundo";
				
				
				
				System.out.println(miFrase.substring(4, 20));
				System.out.println(miFrase.indexOf("Los"));
				System.out.println(miFrase.endsWith("StrIngs!"));
				System.out.println(miFrase.startsWith("PROB"));
				System.out.println(miFrase.replace("o", "*"));
				System.out.println(miFrase.toUpperCase());
				System.out.println(miFrase.toLowerCase());
				System.out.println(miFrase.toCharArray());
		

	}

}
